@tailwind base;
@tailwind components;
@tailwind utilities;

/* Professional Portfolio Design System */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Playfair+Display:wght@400;500;600&display=swap');

@layer base {
  :root {
    /* Masculine Base Colors */
    --background: 0 0% 98%;
    --foreground: 220 15% 15%;

    /* Professional Surfaces */
    --surface: 220 10% 96%;
    --surface-elevated: 0 0% 100%;
    --border-light: 220 15% 88%;
    --text-secondary: 220 10% 45%;

    /* Masculine Brand Colors */
    --primary: 217 91% 40%;  /* Royal Blue */
    --primary-hover: 217 91% 35%;
    --primary-foreground: 0 0% 100%;

    /* Metallic Accents */
    --accent: 220 15% 70%;  /* Metallic Silver */
    --accent-dark: 220 25% 25%;  /* Deep Navy */
    --charcoal: 220 15% 20%;  /* Charcoal */

    /* Gradients */
    --gradient-primary: linear-gradient(135deg, hsl(217 91% 40%), hsl(217 91% 35%));
    --gradient-subtle: linear-gradient(135deg, hsl(220 10% 96%), hsl(0 0% 100%));
    --gradient-hero: linear-gradient(135deg, hsl(220 15% 15%) 0%, hsl(220 25% 25%) 50%, hsl(217 50% 20%) 100%);
    --gradient-metallic: linear-gradient(135deg, hsl(220 15% 70%), hsl(220 10% 80%));

    /* Professional Shadows */
    --shadow-sm: 0 1px 2px 0 hsl(220 15% 15% / 0.08);
    --shadow-md: 0 4px 6px -1px hsl(220 15% 15% / 0.12), 0 2px 4px -1px hsl(220 15% 15% / 0.08);
    --shadow-lg: 0 10px 15px -3px hsl(220 15% 15% / 0.15), 0 4px 6px -2px hsl(220 15% 15% / 0.08);
    --shadow-glow: 0 0 40px hsl(217 91% 40% / 0.25);

    /* Typography */
    --font-heading: 'Playfair Display', serif;
    --font-body: 'Inter', sans-serif;

    /* Shadcn Integration */
    --card: 0 0% 100%;
    --card-foreground: 220 15% 15%;

    --popover: 0 0% 100%;
    --popover-foreground: 220 15% 15%;

    --secondary: 220 15% 96%;
    --secondary-foreground: 220 15% 15%;

    --muted: 220 15% 96%;
    --muted-foreground: 220 10% 50%;

    --accent: 217 91% 40%;  /* Royal Blue */
    --accent-foreground: 0 0% 100%;

    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 15% 88%;
    --input: 220 15% 88%;
    --ring: 217 91% 40%;

    --radius: 0.5rem;

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;

    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;

    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;

    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;

    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;

    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-body;
  }
}

@layer components {
  /* Professional Typography Classes */
  .heading-xl { @apply text-5xl md:text-6xl font-heading font-medium leading-tight; }
  .heading-lg { @apply text-3xl md:text-4xl font-heading font-medium leading-tight; }
  .heading-md { @apply text-2xl md:text-3xl font-heading font-medium leading-tight; }
  .heading-sm { @apply text-xl md:text-2xl font-heading font-medium leading-tight; }

  .body-lg { @apply text-lg leading-relaxed text-muted-foreground; }
  .body-md { @apply text-base leading-relaxed text-muted-foreground; }

  /* Professional Button Variants */
  .btn-hero { 
    @apply bg-gradient-to-r from-primary to-primary-hover text-primary-foreground px-8 py-4 rounded-lg font-semibold hover:shadow-lg transition-all duration-300 hover:scale-105 shadow-md;
  }
  .btn-ghost { 
    @apply border border-border px-8 py-4 rounded-lg font-semibold hover:bg-accent/10 hover:border-accent transition-all duration-300;
  }
  .btn-metallic {
    @apply bg-gradient-to-r from-accent to-accent/80 text-foreground px-8 py-4 rounded-lg font-semibold hover:shadow-lg transition-all duration-300 hover:scale-105;
  }

  /* Custom surfaces */
  .surface { @apply bg-card border border-border rounded-xl; }
  .surface-elevated { @apply bg-surface-elevated shadow-md border border-border-light rounded-xl; }

  /* Animations */
  .animate-fade-up {
    animation: fadeUp 0.6s ease-out forwards;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }
}

@keyframes fadeUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}
